mapper.select:
        SELECT


--         级联表的字段

        FROM zuke  a



                and a.id in

                #{item}



                and a.username like CONCAT('%',#{params.username},'%')


                and a.password like CONCAT('%',#{params.password},'%')


                and a.zuke_name like CONCAT('%',#{params.zukeName},'%')


                and a.zuke_phone like CONCAT('%',#{params.zukePhone},'%')


                and a.zuke_id_number like CONCAT('%',#{params.zukeIdNumber},'%')


                and a.zuke_email like CONCAT('%',#{params.zukeEmail},'%')


                and a.sex_types = #{params.sexTypes}


                and a.danyuan_types = #{params.danyuanTypes}


                and a.shiyong_types = #{params.shiyongTypes}


                  and a.new_money >= #{params.newMoneyStart}   


                  and a.new_money <= #{params.newMoneyEnd}   




        order by a.${params.orderBy} desc
