mapper.select:
SELECT
-- 级联表的字段 ,zuke.zuke_name as zukeName ,zuke.zuke_phone as zukePhone ,zuke.zuke_id_number as zukeIdNumber ,zuke.zuke_email as zukeEmail ,zuke.zuke_photo as zukePhoto ,zuke.danyuan_types as danyuanTypes ,zuke.shiyong_types as shiyongTypes ,zuke.new_money as newMoney
FROM weixiu a left JOIN zuke zuke ON a.zuke_id = zuke.id
and a.id in
#{item}
and a.zuke_id = #{params.zukeId}
and a.weixiu_name like CONCAT('%',#{params.weixiuName},'%')
and a.weixiu_types = #{params.weixiuTypes}
and a.weixiu_content like CONCAT('%',#{params.weixiuContent},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.shifou_types = #{params.shifouTypes}
and a.zuke_id IS NOT NULL
and zuke.zuke_name like CONCAT('%',#{params.zukeName},'%')
and zuke.zuke_phone like CONCAT('%',#{params.zukePhone},'%')
and zuke.zuke_id_number like CONCAT('%',#{params.zukeIdNumber},'%')
and zuke.zuke_email like CONCAT('%',#{params.zukeEmail},'%')
and zuke.danyuan_types = #{params.danyuanTypes}
and zuke.shiyong_types = #{params.shiyongTypes}
and zuke.new_money >= #{params.newMoneyStart}
and zuke.new_money <= #{params.newMoneyEnd}
order by a.${params.orderBy} desc
